mapper.select:
SELECT *
FROM video

    WHERE videoType = ${videoType}

ORDER BY ${orderBy} ${orderDir}

mapper.update:
UPDATE video
SET view_cnt = view_cnt + 1
WHERE video_id = #{video_id}

UPDATE video
SET like_cnt = like_cnt + 1
WHERE video_id = #{video_id}

UPDATE video
SET like_cnt = like_cnt - 1
WHERE video_id = #{video_id}
